// contactsSlice.js
import { createSlice } from '@reduxjs/toolkit';
import * as thunks from '../services/api';

const contactsSlice = createSlice({
  name: 'phonebook',
  initialState: {
    items: [],
    isLoading: false,
    error: null,
  },
  reducers: {
    setContacts: (state, action) => {
      state.items = action.payload;
    },
    setLoading: (state, action) => {
      state.isLoading = action.payload;
    },
    setError: (state, action) => {
      state.error = action.payload;
    },
  },
  extraReducers: builder => {
    builder
      .addCase(thunks.fetchContacts.pending, state => {
        state.isLoading = true;
      })
      .addCase(thunks.fetchContacts.fulfilled, (state, action) => {
        state.items = action.payload;
        state.isLoading = false;
      })
      .addCase(thunks.fetchContacts.rejected, (state, action) => {
        state.error = action.error.message;
        state.isLoading = false;
      })
      .addCase(thunks.addContact.fulfilled, (state, action) => {
        state.items.push(action.payload);
      })
      .addCase(thunks.deleteContact.fulfilled, (state, action) => {
        const idDeleteContact = action.payload.id;
        state.items = state.items.filter(({ id }) => id !== idDeleteContact);
      });
  },
});

export const { setContacts, setLoading, setError } = contactsSlice.actions;
export const contactsReducer = contactsSlice.reducer;

export default contactsReducer;
