import styles from './Loader.module.css';
import { ThreeCircles } from 'react-loader-spinner';

export const Loader = () => (
  <div className={styles['loader-container']}>
    <ThreeCircles
      сolor="grey"
      strokeWidth="5"
      animationDuration="0.75"
      width="96"
      visible={true}
    />
  </div>
);
