import axios from 'axios';

const API_KEY = '65ae13ba1dfbae409a73e084';
const BASE_URL = `https://${API_KEY}.mockapi.io/api`;
const END_POINT_CONTACTS = 'contacts';

axios.defaults.baseURL = BASE_URL;

export const fetchContacts = async () => {
  const { data } = await axios.get(`/${END_POINT_CONTACTS}`);
  return data;
};

export const addContact = async contact => {
  const { data } = await axios.post(`/${END_POINT_CONTACTS}`, contact);
  return data;
};

export const deleteContact = async id => {
  await axios.delete(`/${END_POINT_CONTACTS}/${id}`);
  return id;
};
