import { useDispatch, useSelector } from 'react-redux';
import { deleteContact } from '../../redux/contactsSlice';

import css from './ContactList.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faTrash } from '@fortawesome/free-solid-svg-icons';

export const ContactList = ({ id}) => {
  const dispatch = useDispatch();
  const contacts = useSelector(state => state.contacts.contacts);
  const filter = useSelector(state => state.contacts.filter);

  const getUserContacts = (contacts, filter) => {
    if (!filter) {
      return contacts;
    }

    return contacts.filter(({ name }) => name.toLowerCase().includes(filter));
  };

  const userContacts = getUserContacts(contacts, filter);

  const handleDelete = () => dispatch(deleteContact(id));

  return (
    <ul className={css.list}>
      {userContacts.map((contact) => (
        <li key={contact.id} className={css.item}>
          <div className={css.contactInfo}>
            <div className={css.nameContainer}>
              <FontAwesomeIcon className={css.nameIcon} icon={faUser} />
              <span className={css.name}>{contact.name}:</span>
            </div>
            <span>{contact.number}</span>
          </div>
          <button className={css.deleteButton} onClick={() => handleDelete(contact.id)}>
            <FontAwesomeIcon icon={faTrash} />
            Delete
          </button>
        </li>
      ))}
    </ul>
  );
};

export default ContactList;
