import styles from './Loader.module.css';
import { RotatingLines } from 'react-loader-spinner';

export const Loader = () => (
  <div className={styles['loader-overlay']}>
    <div className={styles['loader-container']}>
      <RotatingLines
        strokeColor="grey"
        strokeWidth="5"
        animationDuration="0.75"
        width="96"
        visible={true}
      />
    </div>
  </div>
);
